static inline void set_bit(u8 * array, u16 flag_id)
{
	flag_id --;
	array[flag_id >> 3] |= 1 << (flag_id & 7);
}

static inline void set_bit2(u8 * array, u16 flag_id)
{
	array[flag_id >> 3] |= 1 << (flag_id & 7);
}

void getForm(struct DexSaveData *pDexAddress, int a2, int a3, int a4, int from)
{
    u16 fromnum, i;
    int v14 = getindexpokemonwith(a2);

    if (v14 != -1)
    {
        if (a2 == 351)
        {
            from = 0;
            fromnum = 4;
        }
        else if (a2 == 351+70)
        {
            from = 0;
            fromnum = 2;
        }
        else if (a2 == 351 + 204)
        {
            from = 0;
            fromnum = 2;
        }
        else if (a2 == 648)
            {
                from = 0;
            fromnum = 2;
            }
        else
        {
            if (from >= getNumberOfForms(a2))
                from = 0;
            fromnum = 1;
        }
        if (a4 == 1)
        {
            for (i = 0; i < fromnum; ++i)
            {
                set_bit2(pDexAddress->from2, from + v14 + i);
            }
        }
        else
        {
            for ( i = 0; i < fromnum; ++i)
            {
                set_bit2(pDexAddress->from1, from + v14 + i);
            }
        }
    }
}

