/* No decompiler yet implemented for arm32 */
u32 EquipmentLand::getItemAmmo(EquipItem itemSelect) {
    bool canUse = (((u32*)this->equipmentBitfield)[(u32)(itemSelect) >> 5] & (1 << (itemSelect & 0x1fU)));

    switch (itemSelect) {
        case EQUIPITEM_BOW:
            return canUse ? this->arrows : 0;
        case EQUIPITEM_BOMBBAG:
            return canUse ? this->bombs : 0;
    }

    return canUse;
}

