struct SavedLevel * SAVE_CreatedSavedLevel(long areaID, struct Level *level) // Matching 90.72%
{
    struct SavedLevel* savedLevel; // $s0
    struct ActualSavedLevel* slevel; // $a0
    long doSave; // $a1
    long i; // $a1
    struct BSPTree* bspTree; // $v1
    long Zoffset; // $s4

    long numBSPTrees; // $s1


    int iVar5;
    struct SavedLevel* pSVar6;
    int iVar7;

    Zoffset = level->terrain->BSPTreeArray->globalOffset.z;
    savedLevel = NULL;
    doSave = FALSE;
    if (level->terrain->UnitChangeFlags & 1 || level->terrain->UnitChangeFlags & 2)
    {
        doSave = TRUE;
    }

    if (doSave)
    {
        numBSPTrees = level->terrain->numBSPTrees - 2;

        if (numBSPTrees > 0)
        {
            savedLevel = SAVE_HasSavedLevel(areaID);
            if(savedLevel != NULL || (savedLevel = (struct SavedLevel *)SAVE_GetSavedBlock(3, numBSPTrees * 8), savedLevel))
            {
                savedLevel->areaID = areaID;
                if (level->waterZLevel == -0x7fff || level->waterZLevel == 0x7fff)
                {
                    savedLevel->waterZ = level->waterZLevel;
                    //savedLevel->waterZ = level->waterZLevel - Zoffset;
                }
                else
                {
                    savedLevel->waterZ = level->waterZLevel - Zoffset;
                    //savedLevel->waterZ = level->waterZLevel;
                }
                savedLevel->numberBSPTreesSaved = numBSPTrees;
                i = 0;

                if (level->terrain->numBSPTrees > 0)
                {
                    //iVar7 = 0;
                    //pSVar6 = (struct ActualSavedLevel*) savedLevel;
                    slevel = savedLevel;
                    bspTree = &level->terrain->BSPTreeArray[0];
                    do
                    {
                        //iVar5 = (int)&level->terrain->BSPTreeArray->bspRoot + iVar7;
                        //if (1 < (unsigned short)(*(short *)(iVar5 + 0x1a) + 1U))
                        //if((unsigned short)(bspTree->instanceSpline->object->id + 1) >= 2U)
                        if(bspTree->ID >= 2U)
                        {
                            slevel->bspTreeArray->bspIndex = 0;

                            memcpy(&slevel->bspTreeArray[0].localOffset, &bspTree->localOffset, sizeof(_Position));

                            slevel->bspTreeArray[0].importantFlagsSaved = bspTree->flags;


                            slevel = (char*)slevel + 8;

                            //bspTree = 

                            //*(int *)((int)&pSVar6[1].numberBSPTreesSaved + 1) = (char)i;
                            //*(int *)(pSVar6 + 1) = *(int *)(iVar5 + 0x14);
                            //pSVar6[1].waterZ = *(short *)(iVar5 + 0x18);
                            //*(int *)&pSVar6[1].numberBSPTreesSaved = *(int *)(iVar5 + 0x12);
                            //pSVar6 = pSVar6 + 1;
                        }

                        //iVar7 = iVar7 + 0x24;
                        bspTree++;
                    } while (level->terrain->numBSPTrees > ++i);
                }
            }
        }
    }
    else
    {
        savedLevel = SAVE_HasSavedLevel(areaID);
        if (savedLevel)
        {
            SAVE_DeleteBlock((struct SavedBasic*)savedLevel);
        }
    }
    return savedLevel;
}
